INSERT INTO recipes (name, ingredients, isHalal, isVegetarian, description, rating) VALUES
	('Ayam Bakar Mantap',array['ayam potong', 'kecap manis', 'jeruk nipis', 'serai', 'garam', 'merica', 'bawang putih', 'bawang merah'],TRUE,FALSE,'Cuci bersih ayam dan lumuri dengan air jeruk nipis dan garam. Tumis bawang merah dan bawang putih yang sudah dihaluskan. Masukkan serai, kecap manis, garam dan merica dan tambahkan air. Masukkan ayam dan masak hingga bumbu meresap. Bakar ayam sambil sesekali dibalik dan diolesi bumbu hingga harum. Angkat dan hidangkan.',5),																								
	('Rendang Sapi dan Ayam',array['daging sapi','daging ayam','santan','cabe keriting','cabe rawit merah','kunyit','lengkuas','bawang merah','bawang putih','daun jeruk','daun salam','garam','gula','bumbu kaldu','ketumbar'],TRUE,FALSE,'Haluskan bumbu-bumbu lalu tumis dengan minyak sayur secukupnya hingga berubah warna. Masukkan daun jeruk dan daun salam, tumis hingga daun agak layu. Masukkan daging sapi yang sudah dipotong-potong lalu tuang santan. Aduk sesekali. Masukkan daging ayam, aduk seterusnya hingga santan sudah mulai mengering. Masak sampai kuah rendang habis dan mengeluarkan minyak. Angkat dan sajikan.',5),																								
	('Nasi Goreng Ayam',array['telur', 'ayam', 'nasi', 'garam', 'merica', 'minyak'],TRUE,FALSE,'Panaskan minyak, masukkan telur dan ayam hingga agak matang. Masukkan nasi, garam dan merica, aduk hingga ayam matang dan semua bumbu tercampur rata. Angkat dan sajikan.',5),																								
	('Tempe Mendoan',array['tempe', 'minyak goreng', 'bawang putih', 'ketumbar', 'garam', 'tepung terigu', 'tepung kanji', 'tepung beras', 'air', 'daun bawang'],TRUE,TRUE,'Lumuri tempe dengan bawang putih, ketumbar, dan garam yang sudah dihaluskan sebagai bumbu perendam. Larutkan semua tepung dengan air, aduk hingga licin. Masukkan ketumbar, bawang putih, dan garam sebagai perasa. Aduk hingga rata. masukkan daun bawang. Panaskan minyak goreng dalam wajan, celup 1 lembar tempe dalam tepung pelapis dan segera masukkan kedalam minyak panas. Goreng sebentar saja hingga kekuningan, angkat dan tiriskan.',5),																								
	('Sayur Asem',array['kangkung', 'kacang jogo segar', 'labu', 'air', 'bawang putih', 'bawang merah', 'cabai merah besar', 'kemiri', 'terasi', 'lengkuas', 'daun salam', 'asam jawa', 'garam', 'gula'],TRUE,TRUE,'Rebus kacang merah sampai lunak. Haluskan bawang putih, bawang merah, cabai merah, kemiri, dan terasi matang. Didihkan air di panci dan masukkan semua bumbu yang telah dihaluskan. Tambahkan daun salam, lengkuas, air asam, garam, dan gula. Masukkan kangkung, labu, dan kacang merah secara bersamaan. Masak hingga matang.',5),																								
	('Bakwan Jagung',array['jagung manis','tepung terigu','bawang putih','bawang merah','seledri','daun bawang','air','garam'],TRUE,TRUE,'Cuci terlebih dahulu jagung dan sisir dengan menggunakan pisau. Masukkan ke dalam wadah dan tambahkan semua bahan dan aduklah hingga merata.Saat menuangkan air pastikan adonan tidak terlalu kental atau cair. Sebaiknya, tuangkan air sedikit demi sedikit untuk mendapatkan konsistensi adonan yang tepat.Panaskan minyak dan goreng adonan bakwan jagung dengan mengambil sebanyak 1 sendok makan.Gorenglah bakwan hingga berwarna kuning keemasan. Lalu sajikan.',5),																								
	('Sate Babi',array['daging babi','lemak babi','bawang merah','bawang putih','cabai merah besar','kemiri','lengkuas','ketumbar','jahe','kunyit','daun jeruk','gula merah','kecap manis','kecap asin','mentega'],FALSE,FALSE,'Potong daging dan lemak babi berbentuk kotak dengan ukuran sesuai selera. Blender semua bumbu hingga halus, dan tumis. Tambahkan kecap manis, kecap asin, dan gula merah hingga masak. Campurkan dengan potongan daging. Tusuk daging dengan tusukkan sate dan kemudian bakar sambil dioleskan sisa bumbu hingga matang.',5),																								
	('Bak kut teh',array['bawang putih', 'biji lada', 'garam', 'vetsin', 'air', 'daging iga babi'],FALSE,FALSE,'Cuci daging iga babi hingga bersih, potong-potong hingga muat dalam slow cooker. Didihkan 2 liter air, masukkan daging iga yang sudah bersih dan didihkan kembali. Angkat dan tiriskan daging iga tersebut. Sangrai bawang putih hingga kuning kecoklatan, taruh dalam slow cooker. Didihkan 2 liter air lagi, masukkan kedalam slow cooker bersama semua bumbu dan daging iga. Nyalakan slow cooker dalam settingan high, tunggu selama 3 jam.',4),																								
	('Chasiu Ramen',array['mie telur','chasiu','nori','telur','daun bawang','jamur','kuah kaldu'],FALSE,FALSE,'Rebus telur hingga setengah matang. Rebus mie dan jamur hingga matang. Siapkan kuah kaldu, masukkan mie, topping telur, chasiu, nori, jamur dan daun bawang sebagai pelengkap.',4),																								
	('Sup Miso',array['miso paste','dashi','tofu','daun bawang','wakame'],TRUE,TRUE,'Seduh wakame dengan air hingga lunak, tiriskan. Didihkan dashi dalam panci, masukkan miso paste, aduk hingga larut. Masukkan tofu, aduk perlahan supaya tidak hancur. Tambahkan wakame, rebus hingga mendidih. Sajikan ke dalam mangkok, tambahkan daun bawang sebagai pelengkap.',5),																								
	('Sate Ayam Gluten',array['terigu protein tinggi','garam','minyak goreng','air','bawang putih','kaldu','lada bubuk','garam','kecap manis','saus sambal'],TRUE,TRUE,'Siapkan terigu protein tinggi, beri garam dan minyak, tuang air dan uleni hingga berbentuk adonan. Diambkan adonan selama 35 menit, kemudian cuci dibawah air mengalir. Lakukan terus hingga air cucian adonan menjadi bening, tiriskan dalam wadah. Didihkan air, masukkan bawang putih, kaldu dan merica bubuk. Masukkan gluten yang dibentuk seperti bakso ato digumpalkan sesuai selera, rebus kira-kira 35 menit dengan api sedang. Tusukkan menjadi sate, kemudian bakar sambil diolesi kecap manis dan saus sambal.',4),																								
	('Soto Ayam',array['ayam kampung','air','minyak','serai','daun salam','daun jeruk','bawang putih','bawang merah','kemiri','kunyit','jahe','merica','garam','tauge','telur','bihun','daun bawang','jeruk nipis'],TRUE,FALSE,'Didihkan air, rebus ayam dengan api kecil hingga hampir lunak. Tumis bumbu halus hingga matang dan harum, masukkan kedalam rebusan ayam hingga ayam lunak, tiriskan dan suwir ayam. Susun ayam, bihun, telur, tauge dalam mangkuk saji, tuangi kaldu panas. Sajikan dengan daun bawang dan jeruk nipis.',4),																								
	('Ayam Goreng Tepung',array['ayam potong','bawang putih','merica','kaldu bubuk','garam','telur','tepung terigu','tepung maizena'],TRUE,FALSE,'Aduk rata ayam, bawang putih, merica, kaldu bubuk dan garam, biarkan hingga meresap. Dalam piring terpisah campur terigu dan maizena, aduk rata. Aduk rata telur dan ayam yang telah dibumbui, gulingkan potongan ayam ke dalam tepung, dan goreng dalam minyak panas sampai kuning kecoklatan.',5),																								
	('Salmon Mentai',array['salmon', 'mayonaise', 'nasi', 'wijen', 'merica', 'mentaiko','shoyu','nori'],TRUE,FALSE,'Campurkan nasi dengan wijen dan nori kemudian panggang salmon di atas teflon sampai berubah warna angkat dan letakan di atas nasi. lumurkan campuran saus mayonaise,shoyu dan tobiki di atas salmon hingga merata. Bakar saus dengan blowtorch ',3),																								
	('Beef Yakiniku',array['daging sapi', 'paprika', 'bawang bombai', 'kecap manis', 'garam', 'lada', 'minyak'],TRUE,FALSE,'Potong daging, paprika dan bawang bombai sesuai selera. Panaskan wajan dengan minyak secukupnya, masukkan bawang bombai dan paprika, tumis sebentar. Masukkan daging sapi, kecap, dan garam, tambahkan sedikit air jika sudah mengering. Angkat dan sajikan.',3),																								
	('Burger Tempe',array['tempe', 'paprika', 'bawang bombai', 'mayonaise', 'garam', 'lada', 'kecap asin','selada','margarin'],TRUE,TRUE,'Oleskan margarine di penggorengan kemudian panaskan. panggang roti burger sampai kecoklatan. panggang tempe yang sudah dimarinasi kecap manis dan bawang bombai hingga harum  dan berubah warna. oleskan mayones atau sambal sesuai selera pada roti burger dan susun seluruh bagian',5),																								
